import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';

let socket;

export default function Connect({ user, token }) {
  const [status, setStatus] = useState('idle');
  const [partner, setPartner] = useState(null);
  const [roomId, setRoomId] = useState(null);

  useEffect(() => {
    socket = io('http://localhost:5000');
    socket.on('connect', () => {
      console.log('socket connected', socket.id);
      // tell server who we are
      socket.emit('iamonline', { userId: user.id });
    });

    socket.on('waiting', () => {
      setStatus('waiting');
    });

    socket.on('matched', ({ roomId, partner }) => {
      setStatus('matched');
      setPartner(partner);
      setRoomId(roomId);
      // navigate to call page - we will reuse a simple render switch: show call UI below
    });

    socket.on('disconnect', () => {
      setStatus('disconnected');
    });

    return () => {
      if (socket) socket.disconnect();
    };
  }, []);

  const start = () => {
    setStatus('searching');
    socket.emit('findPartner', { userId: user.id });
  };

  const stop = () => {
    if (socket) socket.emit('leave', { userId: user.id, roomId });
    setStatus('idle');
    setPartner(null);
    setRoomId(null);
  };

  return (
    <div>
      <h2>Connect</h2>
      <p>Status: {status}</p>
      {status === 'idle' && <button onClick={start}>Connect</button>}
      {(status === 'waiting' || status === 'searching') && <button onClick={stop}>Cancel</button>}
      {status === 'matched' && partner && (
        <div>
          <h3>Connected with {partner.username}</h3>
          <button onClick={() => window.location.reload()}>Go to Call</button>
          <p>Room: {roomId}</p>
        </div>
      )}
    </div>
  );
}
