import React, { useState, useEffect } from 'react';
import Register from './pages/Register';
import Login from './pages/Login';
import Profile from './pages/Profile';
import Connect from './pages/Connect';
import Call1 from './pages/Call';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || null);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user') || 'null'));
  const [page, setPage] = useState('login');

  useEffect(() => {
    if (token && user) setPage('profile');
  }, [token, user]);

  const handleLogin = ({ token, user }) => {
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(user));
    setToken(token);
    setUser(user);
    setPage('profile');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setToken(null);
    setUser(null);
    setPage('login');
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>OpenTalk - Voice</h1>
      <div style={{ marginBottom: 10 }}>
        {token ? (
          <>
            <button onClick={() => setPage('profile')}>Profile</button>
            <button onClick={() => setPage('connect')}>Connect</button>
            <button onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <>
            <button onClick={() => setPage('login')}>Login</button>
            <button onClick={() => setPage('register')}>Register</button>
          </>
        )}
      </div>

      {page === 'register' && <Register afterRegister={handleLogin} />}
      {page === 'login' && <Login afterLogin={handleLogin} />}
      {page === 'profile' && <Profile user={user} setUser={setUser} token={token} />}
      {page === 'connect' && <Connect user={user} token={token} />}
   {page === 'call' && <Call1 user={user} token={token} />}
    </div>
  );
}

export default App;
