import React, { useEffect, useRef, useState } from 'react';
import socket from '../socket';

let pc;

export default function Call1({ user, roomInfo, setPage }) {
  const remoteAudio = useRef();
  const [inCall, setInCall] = useState(false);
  const [partner, setPartner] = useState(roomInfo.partner || null);
  const [roomId, setRoomId] = useState(roomInfo.roomId || null);

  useEffect(() => {
    socket.emit('iamonline', { userId: user.id });

    if (roomId && partner) {
      startPeer(roomId).catch(console.error);
    }

    const onOffer = async (offer) => {
      if (!pc) await startPeer(roomId, false);
      await pc.setRemoteDescription(offer);
      const answer = await pc.createAnswer();
      await pc.setLocalDescription(answer);
      socket.emit('answer', { roomId, answer });
    };

    const onAnswer = async (answer) => {
      if (pc) await pc.setRemoteDescription(answer);
    };

    const onIceCandidate = async (candidate) => {
      if (pc) await pc.addIceCandidate(candidate);
    };

    socket.on('offer', onOffer);
    socket.on('answer', onAnswer);
    socket.on('ice-candidate', onIceCandidate);

    return () => {
      if (pc) pc.close();
      pc = null;
      socket.off('offer', onOffer);
      socket.off('answer', onAnswer);
      socket.off('ice-candidate', onIceCandidate);
      window.onbeforeunload = null;
    };
  }, [roomId, partner]);

  const startPeer = async (rId, initiator = true) => {
    pc = new RTCPeerConnection();
    setInCall(true);

    let stream;
    try {
      stream = await navigator.mediaDevices.getUserMedia({ audio: true, video: false });
    } catch (err) {
      console.error('getUserMedia failed', err);
      setInCall(false);
      return;
    }

    stream.getTracks().forEach(track => pc.addTrack(track, stream));

    pc.ontrack = (e) => {
      if (remoteAudio.current) remoteAudio.current.srcObject = e.streams[0];
    };

    pc.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit('ice-candidate', { roomId: rId, candidate: event.candidate });
      }
    };

    if (initiator) {
      const offer = await pc.createOffer();
      await pc.setLocalDescription(offer);
      socket.emit('offer', { roomId: rId, offer });
    }

    window.onbeforeunload = () => {
      socket.emit('leave', { userId: user.id, roomId: rId });
    };
  };

  const leave = () => {
    if (pc) pc.close();
    pc = null;
    if (roomId) socket.emit('leave', { userId: user.id, roomId });
    setInCall(false);
    setPartner(null);
    setRoomId(null);
    window.onbeforeunload = null;

    if (setPage) setPage('profile');
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Call</h2>
      {partner && <p style={styles.partner}>Partner: {partner.username}</p>}
      <audio ref={remoteAudio} autoPlay style={styles.audio} />
      {!inCall && <p style={styles.status}>Waiting to join call...</p>}
      {inCall && (
        <button style={styles.disconnectBtn} onClick={leave}>Disconnect</button>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: 500,
    margin: '50px auto',
    padding: 20,
    borderRadius: 10,
    boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
    backgroundColor: '#fff',
    textAlign: 'center',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  title: {
    color: '#007BFF',
    fontSize: 28,
    marginBottom: 15,
  },
  partner: {
    fontSize: 18,
    marginBottom: 15,
  },
  audio: {
    width: '100%',
    marginBottom: 15,
  },
  status: {
    fontSize: 16,
    marginBottom: 15,
  },
  disconnectBtn: {
    padding: '10px 25px',
    fontSize: 16,
    backgroundColor: '#dc3545',
    color: '#fff',
    border: 'none',
    borderRadius: 6,
    cursor: 'pointer',
    transition: '0.2s',
  },
};
