import React, { useEffect, useState } from 'react';
import socket from '../socket';

export default function Connect({ user, token, setPage }) {
  const [status, setStatus] = useState('idle');
  const [partner, setPartner] = useState(null);
  const [roomId, setRoomId] = useState(null);

  useEffect(() => {
    socket.emit('iamonline', { userId: user.id });

    const onWaiting = () => setStatus('waiting');
    const onMatched = ({ roomId, partner }) => {
      setStatus('matched');
      setPartner(partner);
      setRoomId(roomId);
    };

    socket.on('waiting', onWaiting);
    socket.on('matched', onMatched);

    return () => {
      socket.off('waiting', onWaiting);
      socket.off('matched', onMatched);
    };
  }, []);

  const start = () => {
    setStatus('searching');
    socket.emit('findPartner', { userId: user.id });
  };

  const stop = () => {
    if (roomId) socket.emit('leave', { userId: user.id, roomId });
    setStatus('idle');
    setPartner(null);
    setRoomId(null);
  };

  return (
    <div>
      <h2>Connect</h2>
      <p>Status: {status}</p>
      {status === 'idle' && <button onClick={start}>Connect</button>}
      {(status === 'waiting' || status === 'searching') && <button onClick={stop}>Cancel</button>}
      {status === 'matched' && partner && (
        <div>
          <h3>Connected with {partner.username}</h3>
          <button onClick={() => setPage('call')}>Go to Call</button>
          <p>Room: {roomId}</p>
        </div>
      )}
    </div>
  );
}
