import React, { useEffect, useRef, useState } from 'react';
import socket from '../socket'; // <-- yahan se lo, naya mat banao

let pc;

export default function Call({ user }) {
  const remoteAudio = useRef();
  const [inCall, setInCall] = useState(false);
  const [partner, setPartner] = useState(null);
  const [roomId, setRoomId] = useState(null);

  useEffect(() => {
    // mark online
    socket.emit('iamonline', { userId: user.id });

    socket.on('matched', async ({ roomId, partner }) => {
      setPartner(partner);
      setRoomId(roomId);
      await startPeer(roomId);
    });

    socket.on('offer', async (offer) => {
      if (!pc) await startPeer(roomId, false);
      await pc.setRemoteDescription(offer);
      const answer = await pc.createAnswer();
      await pc.setLocalDescription(answer);
      socket.emit('answer', { roomId, answer });
    });

    socket.on('answer', async (answer) => {
      if (pc) await pc.setRemoteDescription(answer);
    });

    socket.on('ice-candidate', async (candidate) => {
      if (pc) await pc.addIceCandidate(candidate);
    });

    return () => {
      // ðŸ‘‡ yahan disconnect mat karo
      if (pc) pc.close();
    };
  }, []);

  const startPeer = async (rId, initiator = true) => {
    pc = new RTCPeerConnection();
    setInCall(true);

    // get only audio
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true, video: false });
    stream.getTracks().forEach(track => pc.addTrack(track, stream));

    pc.ontrack = (e) => {
      remoteAudio.current.srcObject = e.streams[0];
    };

    pc.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit('ice-candidate', { roomId: rId, candidate: event.candidate });
      }
    };

    if (initiator) {
      const offer = await pc.createOffer();
      await pc.setLocalDescription(offer);
      socket.emit('offer', { roomId: rId, offer });
    }

    window.onbeforeunload = () => {
      socket.emit('leave', { userId: user.id, roomId: rId });
    };
  };

  const leave = () => {
    if (pc) pc.close();
    socket.emit('leave', { userId: user.id, roomId });
    setInCall(false);
    setPartner(null);
    setRoomId(null);
  };

  return (
    <div>
      <h2>Call</h2>
      {partner && <p>Partner: {partner.username}</p>}
      <audio ref={remoteAudio} autoPlay />
      {!inCall && <p>Waiting to join call...</p>}
      {inCall && <button onClick={leave}>Disconnect</button>}
    </div>
  );
}
